import React, {useContext, useEffect} from 'react';
import  MovieContext from './context';
import Recommendation from './Recommendation';
import {fetchDetail} from './service';


const Result = () =>{
	const [movieState, setMovieState]  = useContext(MovieContext);
	useEffect(()=>{
		if(movieState.id){
			const imgURL = "https://image.tmdb.org/t/p/w500/";
			console.log("change result");
			fetchDetail(movieState.id)
			.then(json=>{
				setMovieState({
					Title: json.title,
					Year:json.release_date,
					imdbID: json.imdb_id, 
					id: json.id,
					PosterUrl: imgURL + json.poster_path})
			})
		}
	},[movieState.id])


	return (
		<div>
		<p> Title: {movieState.Title} </p>
		<p> Year: {movieState.Year} </p>
		<img src= {movieState.PosterUrl} alt='poster'/> 
		<label for="commet">Comments:</label>
		<textarea id="comment" rows="4" cols ="10" ></textarea>
		<Recommendation />
		</div>)
}

export default Result;
