import React, { useContext, useState, useEffect } from "react";
import { MovieContext, ErrorContext } from "./context";
import errors from "./errors";
import Recommendation from "./Recommendation";
import { fetchDetail, convertImdbIdtoId } from "./service";
import unavailablePic from "./unavailable.jpg";

const Result = () => {
  const [movieState, setMovieState] = useContext(MovieContext);
  const [error, setError] = useContext(ErrorContext);
  const [comment, setComment] = useState("");

  useEffect(() => {
    if (movieState.id) {
      getDetail(movieState.id);
      setError("");
    }
    if (!movieState.id) {
      convertImdbIdtoId(movieState.imdbID)
        .then((json) => {
          if (json.movie_results.length === 0) {
            throw new Error("LACK_KEYWORD");
          }
          const id = json.movie_results[0].id;
          getDetail(id);
          setError("");
        })
        .catch((err) => {
          setError(errors[err.message || err || "DEFAULT"]);
        });
    }
  }, [movieState.id]);

  const getDetail = (id) => {
    const imgURL = "https://image.tmdb.org/t/p/w500/";
    return fetchDetail(id)
			.then((json) => {
				if (!json.poster_path)
          setMovieState({
            Title: json.title,
            Year: json.release_date,
            imdbID: json.imdb_id,
            id: json.id,
            overview: json.overview,
            PosterUrl: json.poster_path
              ? imgURL + json.poster_path
              : unavailablePic,
          });
        setError("");
      })
      .catch((err) => {
        setError(errors[err.message || "DEFAULT"]);
      });
  };
  const handleInput = (e) => {
    setComment(e.target.value);
  };
	const sendCommentToServer = () => {
		console.log(comment);
	};
  return (
    <div className="container">
      <div className="flex-box">
        <div className="result-detail">
          <p> Title: {movieState.Title} </p>
          <p className="time"> Year: {movieState.Year} </p>
          <p className="overview"> Overview: {movieState.overview}</p>
          <label>
            My Comment:
            <textarea
              id="comment"
              rows="4"
              cols="50"
              value={comment}
              onChange={(e) => handleInput(e)}
            ></textarea>
          </label>
          <button onClick={sendCommentToServer}>add/update</button>
        </div>
        <div className="img-detail">
          <img src={movieState.PosterUrl} alt="poster" />
        </div>
      </div>

      <Recommendation />
    </div>
  );
};

export default Result;
