import React, {useState, useEffect} from 'react';
import Login from './Login';
import Recommendation from './Recommendation';
import Search from './Search';
import errors from './errors';
import './App.css';

function App() {
  const [userState, setUserState] = useState({ isLoggedIn: false });
  const [movieState, setMovieState] = useState({keyword:null, PosterUrl: null});

  const login = (username) => {
    setUserState({
      isLoggedIn: true,
      username
    });
  };

  const logout = () => {
    setUserState({
      isLoggedIn: false
    });
  };

  const seachOmdb = () =>{
    const OMDB = "https://www.omdbapi.com/?apikey=c27a2924&s="
    const url = OMDB + movieState.keyword;
    fetchOMDB(url)
    .then(json =>{
      setMovieState({ ...movieState, PosterUrl: json.Search[0].Poster})})
  }
  let content;
  if (!userState.isLoggedIn){
    content = (<div>
                <Login onLogin = { login} />
                <Recommendation />
              </div>)
  } else{
    content = (<Search onSearch = {searchOmdb} setMovieState ={setMovieState} />
            )
  }

  return (
    <div className="App">
      {content}
    </div>
  );
}

export default App;
