const convertNetworkError = () => Promise.reject( { error: 'NETWORK_ERROR' });

const convertError = (response) =>{
    if(response.ok){
      return response.json();
    }
    return response.json().then(err => Promise.reject(err));
};

export const fetchTopRated = () =>{
  const TMDB ="https://api.themoviedb.org/3/movie/top_rated?api_key=c946c30088ad16d4120dcc22233c5fdc&language=en-US&page=1";
  return fetch(TMDB, {
    method:"GET"})
  .catch( convertNetworkError )
  .then( convertError );
}

export const convertImdbIdtoId = (imdbId) =>{
  console.log(imdbId);
  const TMDB = `https://api.themoviedb.org/3/find/{imdbId}?api_key=c946c30088ad16d4120dcc22233c5fdc&language=en-US&external_source=imdb_id`;
  fetch(TMDB, {
    method:"GET"})
  .catch( convertNetworkError )
  .then( convertError )
  .then(json =>{
    return json.movie_results[0].id;
  });

}

export const fetchRecommendation = (id) =>{
  const TMDB =`https://api.themoviedb.org/3/movie/{id}/recommendations?api_key=c946c30088ad16d4120dcc22233c5fdc&language=en-US&page=1`;
  return fetch(TMDB, {
    method:"GET"})
  .catch( convertNetworkError )
  .then( convertError );
}

export const fetchOMDB = (keyword) =>{
  const OMDB = "https://www.omdbapi.com/?apikey=c27a2924&s="
  const url = OMDB + keyword;
  return fetch(url, {
    method:'GET'})
  .catch( convertNetworkError )
  .then( convertError );
}

export const fetchLoginStatus = (username) => {
  return fetch('/session', {
    method: 'GET',
  })
  .catch( convertNetworkError )
  .then( convertError );
};

export const fetchLogin = (username) => {
  return fetch('/session', {
    method: 'POST',
    headers: new Headers({
      'content-type': 'application/json',
    }),
    body: JSON.stringify({ username }),
  })
  .catch( convertNetworkError )
  .then( convertError );
};

export const fetchLogout = () => {
  return fetch('/session', {
    method: 'DELETE',
  })
  .catch( convertNetworkError)
  .then( response => {
    return response.ok;
  });
};
export  const getMessagesFromServer = () =>{
  return fetch('/messages',{method:"GET"})
    .catch( convertNetworkError )
    .then( convertError );
  };

export const sendMessageToServer = (formedMessage)=>{
  return  fetch('/messages', {method:'POST', 
        headers:{'Content-Type':'application/json'}, 
        body:JSON.stringify(formedMessage)
      })
      .catch( convertNetworkError )
      .then( convertError )
  };

  export  const getUsersFromServer = () =>{
  return fetch('/users',{method:"GET"})
    .catch(convertNetworkError)
    .then( convertError );
  };
