import React, {useState, useEffect} from 'react';
import Login from './Login';
import Search from './Search';
import errors from './errors';
import  MovieContext from './context';
import { fetchLoginStatus, fetchOMDB } from './service';

import './App.css';

function App() {
  const [userState, setUserState] = useState({ isLoggedIn: true });
  const [movieState, setMovieState] = useState({});

  const login = (username) => {
    setUserState({
      isLoggedIn: true,
      username
    });
  };

  const logout = () => {
    setUserState({
      isLoggedIn: false
    });
  };


  let content;
  if (!userState.isLoggedIn){
    content = (<div>
                <Login onLogin = { login} />

              </div>)
  } else{
    console.log(movieState);
    content = (<MovieContext.Provider value= {[movieState, setMovieState]}>
                <Search  />
              </MovieContext.Provider>
            )
  }

  return (
    <div className="App">
      <h1>Your </h1>
      {content}
    </div>
  );
}

export default App;
