import React, {useContext} from 'react';
import  MovieContext from './context';
import Recommendation from './Recommendation';
import {fetchDetail} from './service';

const Result = () =>{
	const [movieState, setMovieState]  = useContext(MovieContext);

	if(movieState.id){
		const imgURL = "https://image.tmdb.org/t/p/w500/";

		fetchDetail(movieState.id)
		.theb(json=>{
			setMovieState({Title: json.title, Year:json.release_date, imdbID: json.imdb_id, PosterUrl: imgURL + json.poster_path})
		})
	}

	return (
		<div>
		<p> Title: {movieState.Title} </p>
		<p> Year: {movieState.Year} </p>
		<img src= {movieState.PosterUrl} alt='poster'/>  
		<Recommendation />
		</div>)
}

export default Result;
