import React, {useState, useEffect} from 'react';
import Login from './Login';
import Recommendation from './Recommendation';
import Search from './Search';
import errors from './errors';
import  MovieContext from './context';
import { fetchLoginStatus, fetchOMDB } from './service';

import './App.css';

function App() {
  const [userState, setUserState] = useState({ isLoggedIn: true });
  const [movieState, setMovieState] = useState({imdbID: null, keyword:null, PosterUrl: null});

  const login = (username) => {
    setUserState({
      isLoggedIn: true,
      username
    });
  };

  const logout = () => {
    setUserState({
      isLoggedIn: false
    });
  };

  const searchOmdb = () =>{
    const OMDB = "https://www.omdbapi.com/?apikey=c27a2924&s="
    const url = OMDB + movieState.keyword;
    console.log(url);
    fetchOMDB(url)
    .then(json =>{
      setMovieState({ 
        imdbID:json.Search[0].imdbID, 
        keyword:null,
        PosterUrl:json.Search[0].Poster
      })
      console.log(movieState);
    })
  }

  let content;
  if (!userState.isLoggedIn){
    content = (<div>
                <Login onLogin = { login} />
                <Recommendation />
              </div>)
  } else{
    content = (<MovieContext.Provider value= {[movieState, setMovieState]}>
              <Search onSearch = {searchOmdb} />
              </MovieContext.Provider>
            )
  }

  return (
    <div className="App">
      {content}
    </div>
  );
}

export default App;
