import React, {useState, useEffect} from 'react';
import Login from './Login';
import Search from './Search';
import errors from './errors';
import  MovieContext from './context';
import { fetchLoginStatus, fetchOMDB } from './service';

import './App.css';

function App() {
  const [userState, setUserState] = useState({ isLoggedIn: true });
  const [movieState, setMovieState] = useState({ keyword:null});

  const login = (username) => {
    setUserState({
      isLoggedIn: true,
      username
    });
  };

  const logout = () => {
    setUserState({
      isLoggedIn: false
    });
  };

  const searchOmdb = () =>{

    fetchOMDB(movieState.keyword)
    .then(json =>{
      setMovieState({ ...movieState, 
        imdbID:json.Search[0].imdbID,
        Title: json.Search[0].Title,
        PosterUrl:json.Search[0].Poster,
        Year:json.Search[0].Year
      })
    })
  };

  let content;
  if (!userState.isLoggedIn){
    content = (<div>
                <Login onLogin = { login} />

              </div>)
  } else{
    content = (<MovieContext.Provider value= {[movieState, setMovieState]}>
                <Search onSearch = {searchOmdb} />
              </MovieContext.Provider>
            )
  }

  return (
    <div className="App">
      {content}
    </div>
  );
}

export default App;
